import React, { useState, useEffect } from "react";
import { useDispatch } from "react-redux";
import { Navigate } from "react-router-dom";
import { loginAuthAction } from "../../redux/actions/authActions";
import Cookies from "js-cookie";
import jwt_decode from "jwt-decode";
const LoginComponents = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [user, setUsers] = useState();
  const dispatch = useDispatch();
  // const { authReducersResult } = useSelector((state) => state.contactReducers);
  const handleSubmit = (event) => {
    event.preventDefault();
    console.log("1. Hendle Keaction");
    dispatch(loginAuthAction({ email: email, password: password }));
  };

  useEffect(() => {
    //const getToken = Cookies.get("token") ? JSON.parse(getToken) : "";
   
    //console.log(token);
    // if (token) {
    //   console.log(token);
    //   // const decode = setUsers(jwt_decode(token));
    //   // console.log(decode);
    // } else {
    //   console.log();
    //   //Navigate("/");
    // }
  }, []);
  return (
    //{console.log(token)}
    <div>
       const token = Cookies.get("token") ? JSON.parse(Cookies.get("token")) : "";
      {user}
      {console.log(token)}

      <form onSubmit={(event) => handleSubmit(event)}>
        <input
          type="text"
          name="email"
          placeholder="email ..."
          value={email}
          onChange={(event) => setEmail(event.target.value)}
        />
        <br />
        <input
          type="password"
          name="password"
          placeholder="password ..."
          value={password}
          onChange={(event) => setPassword(event.target.value)}
        />
        <br />
        <button type="submit"> Login </button>
      </form>
    </div>
  );
};

export default LoginComponents;
