import { LOGIN_SUCCESS } from "../../types";
import { getCookie } from "../../../utils/setCookie";

const initialState = {
  isLoggedIn: !!getCookie(),
  user: getCookie(),
};

const auth = (state = initialState, action) => {
  console.log(actionuser);
  switch (action.type) {
    case LOGIN_SUCCESS:
      return {
        ...state,
        isLoggedIn: true,
        user: action.payload.user,
      };
    default:
      return state;
  }
};

export default auth;
