import axios from "axios";
import { ACCESS_TOKEN_USER } from "../types";

export const getUsers = (accessToken) => {
  return async (dispatch) => {
    try {
      const req = await axiosPrivate.post("/auth", data);
      dispatch({
        type: LOGIN_SUCCESS,
        payload: { users: req.data },
      });
      setCookie(req.data);
      return req;
    } catch (error) {
      console.log(error);
    }
    //console.log(accessToken);
    const res = await axios({
      method: "POST",
      url: "http://localhost:5000/api/checkusers/",
      timeout: 120000,
      headers: {
        authorization: `Bearer ${accessToken}`,
      },
    });
   // console.log(res.data);
    dispatch({
      type: ACCESS_TOKEN_USER,
      payload: {
        loading: true,
        data: res.data,
        errorMessage: false,
      },
    });
  };
};
