import { LOGIN_SUCCESS } from "../../types";
//import { getCookie } from "../../../utils/setCookie";

const initialState = {
  isLoggedIn: false,
  users: false,
};

const auth = (state = initialState, action) => {
  switch (action.type) {
    case LOGIN_SUCCESS:
      return {
        ...state,
        isLoggedIn: true,
        users: action.payload.users,
      };
    default:
      return state;
  }
};

export default auth;
