import React, { useState, useEffect } from "react";
import { useDispatch } from "react-redux";
import { Navigate } from "react-router-dom";
import { loginAuthAction } from "../../redux/actions/authActions";
import Cookies from "js-cookie";
import jwt_decode from "jwt-decode";
const LoginComponents = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [user, setUsers] = useState();
  const dispatch = useDispatch();
  const { authReducersResult } = useSelector((state) => state.contactReducers);
  const handleSubmit = (event) => {
    event.preventDefault();
    console.log("1. Hendle Keaction");
    dispatch(loginAuthAction({ email: email, password: password }));
  };
  // const token = Cookies.get("token") ? JSON.parse(Cookies.get("token")) : "";
  // console.log(token);
  useEffect(() => {
    if (authReducersResult) {
      const token = JSON.parse(Cookies.get("token"));
      const decode = setUsers(jwt_decode(token));
      Navigate("/");
    }
  }, [authReducersResult, dispatch]);

  return (
    <div>
      {user && <Navigate to="/" />}

      <form onSubmit={(event) => handleSubmit(event)}>
        <input
          type="text"
          name="email"
          placeholder="email ..."
          value={email}
          onChange={(event) => setEmail(event.target.value)}
        />
        <br />
        <input
          type="password"
          name="password"
          placeholder="password ..."
          value={password}
          onChange={(event) => setPassword(event.target.value)}
        />
        <br />
        <button type="submit"> Login </button>
      </form>
    </div>
  );
};

export default LoginComponents;
