import axiosInstance from "./useAxios";
import { useEffect } from "react";
import { getCookie } from "./setCookie";

const useAxiosPrivate = (store) => {
  axiosInstance.interceptors.request.use(
    (config) => {
      const users = getCookie();
      if (users) {
        config.headers["Authorization"] = "Bearer " + users?.accessToken; // for Spring Boot back-end
      }
      return config;
    },
    (error) => {
      return Promise.reject(error);
    }
  );

  const { dispatch } = store;
  axiosInstance.interceptors.response.use(
    (res) => {
      return res;
    },
    async (err) => {
      
      const originalConfig = err.config;

      if (originalConfig.url !== "/auth/" && err.response) {
        // Access Token was expired
        if (err.response.status === 401 && !originalConfig._retry) {
          originalConfig._retry = true;

          try {
            const rs = await axiosInstance.post("/refresh_token/", {
              refreshToken: TokenService.getLocalRefreshToken(),
            });

            const { accessToken } = rs.data;

            dispatch(refreshToken(accessToken));
            TokenService.updateLocalAccessToken(accessToken);

            return axiosInstance(originalConfig);
          } catch (_error) {
            return Promise.reject(_error);
          }
        }
      }

      return Promise.reject(err);
    }
  );
};

export default useAxiosPrivate;
