import axios from "axios";
import useAxiosPrivate from "./useAxiosPrivate";

const BASE_URL = "http://localhost:5000/api";

const axiosInstance = axios.create({
  baseURL: BASE_URL,
  headers: {
    "Content-Type": "application/json",
  },
});

axiosInstance.interceptors.request.use(
  (config) => {
    if (users) {
      config.headers["authorization"] = `Bearer ${users?.accessToken}`;
    }
    return config;
  },
  (error) => Promise.reject(error)
);

axiosInstance.interceptors.response.use(
  (response) => response,
  async (error) => {
    const originalConfig = error.config;
    if (originalConfig.url !== "/checkusers" && error.response) {
      if (error.response.status === 401 && !originalConfig._retry) {
        originalConfig._retry = true;

        try {
          const req = await axiosInstance.post("/refresh_token/", {
            refreshToken: users.refreshTOken,
          });

          const { accessToken } = req.data;
          console.log(accessToken);
          dispatch(refreshTokenAction(accessToken));
          return axiosInstance(originalConfig);
        } catch {
          return Promise.reject(error);
        }
      }
    }
    return Promise.reject(error);
  }
);
export default axiosInstance;
