import axios from "axios";

const BASE_URL = "http://localhost:5000/api";
const users = getCookie();
export default axios.create({
  baseURL: BASE_URL,
});

export const axiosPrivate = axios.create({
  baseURL: BASE_URL,
  headers: { Authorization: 'Bearer' + users?.access }
});
