import { LOGIN_SUCCESS } from "../../types";
import { getCookie } from "../../../utils/setCookie";

const initialState = {
  isLoggedIn: true,
  users: getCookie(),
};

const auth = (state = initialState, action) => {
  switch (action.type) {
    case LOGIN_SUCCESS:
      return {
        ...state,
        isLoggedIn: true,
        users: action.payload.users,
      };
      case REFRESH_TOKEN_SUCCESS:
        return {
          ...state,
          isLoggedIn: true,
          users: action.payload.users,
        };
    default:
      return state;
  }
};

export default auth;
